							"eid": "4GeNq6iE3u2l8kS1ZN7xiB",
							"type": "authorPaper",
							"text": "Low-latency MLLM Inference with Spatiotemporal Heterogeneous  Distributed Multimodal Data",
							"articleLocation": "pdfs/CSCAIoT2024-61UGpCVIfVW6bfns5N4QQy/633800a019/633800a019.pdf",
